import React, { useState } from 'react';

function BookDemo() {
    const [showForm, setShowForm] = useState(false);

    const toggleForm = () => {
        setShowForm(!showForm);
    };

    return (
        <>
            <a href="/" className='hidden lg:flex items-center bg-purple-100 hover:bg-purple-700 transition-all duration-300 rounded py-2 px-4 text-purple-800 hover:text-white  ml-52'><span>Log in</span></a>
            <button className='hidden lg:flex items-center bg-purple-100 hover:bg-purple-700 transition-all duration-300 rounded py-2 px-4 text-purple-800 hover:text-white  ml-52' onClick={toggleForm}><span>Book Demo</span></button>
            {showForm && (
                <div className="fixed top-0 left-0 w-[1300px] h-full flex items-center justify-center bg-white ">
                    <div className="bg-white p-8 rounded shadow-lg ">
                        <h2 className="text-2xl font-bold mb-4">Book a Demo</h2>
                        <form className='w-[1300px]'>
                            <div className="mb-4">
                                <label htmlFor="name" className="block text-gray-700 text-sm font-bold mb-2">Name:</label>
                                <input type="text" id="name" name="name" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                            </div>
                            <div className="mb-4">
                                <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">Email:</label>
                                <input type="email" id="email" name="email" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline" />
                            </div>
                            <div className="mb-6">
                                <label htmlFor="message" className="block text-gray-700 text-sm font-bold mb-2">Message:</label>
                                <textarea id="message" name="message" rows="4" className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"></textarea>
                            </div>
                            <div className="flex justify-end">
                                <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">Submit</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
}

export default BookDemo;
