import React from 'react';
import { useState } from 'react';
import { GrLanguage } from "react-icons/gr";
import { FaXmark } from "react-icons/fa6";
import { FaBars } from "react-icons/fa6";
function Navbar() {
    const [ismenuopen, setIsmenuopen] = useState(false)
    const togglemnu=()=>{
        setIsmenuopen(!ismenuopen)
    }
    
    const navitems=[
        {link:'Home',path:'home'},
        {link:'About',path:'about'},
        {link:'Menu',path:'menu'},
    ]

   
  return (
   <nav className='bg-white md:px-14 py-2 max-w-screen-2xl mx-auto text-purple-600'>
    <div className=' container mx-auto flex justify-between items-center font-medium'>
        <div className='  flex  space-x-14 items-center'>
            <a href="/"className=' font-semibold text-2xl flex item-center space-x-3' ></a>
     <ul className='md:flex space-x-12 hidden'>
    {
        navitems.map(({link,path})=>(
            <a key={link}  href={path} className=' block hover:text-gray-300'>{link}</a>
        ))
    }
    
</ul>
</div>
 
    <div className='space-x-12 hidden md:flex items-center '>
    <a href="/" className='hidden lg:flex items-center hover:text-purple-700 ml-52'><GrLanguage className='mr-2 mt-1'/><span c>Language</span></a>
    <button className='bg-purple-100 hover:bg-purple-700 transition-all duration-300 rounded py-2 px-4 text-purple-800 hover:text-white ml-52'>Sign up</button>
    </div>
    <div>
        <button onClick={togglemnu} className='text-white focus:outline-none focus:text-gray-300'>

            {ismenuopen ? (<FaXmark className='w-6 h-6 text-purple-500'/>) :(<FaBars className='w-6 h-6 text-purple-500'/>)}
        </button>
    </div>
    </div>
</nav>
  )
}

export default Navbar
