// export default Navbar;
import React, { useState } from 'react';
import { FaBars, FaXmark } from "react-icons/fa6";
import atom from '../assets/atom.png';

function Navbar() {
    const [ismenuopen, setIsmenuopen] = useState(false);
    const [showForm, setShowForm] = useState(false);
    const [showLogin, setShowLogin] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        email: ''
    });

    const toggleMenu = () => {
        setIsmenuopen(!ismenuopen);
    };

    const toggleForm = () => {
        setShowForm(!showForm);
        setShowLogin(false); // Giriş formunu kapat
    };

    const toggleLogin = () => {
        setShowLogin(!showLogin);
        setShowForm(false); // Demo formunu kapat
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData); // Form verilerini işleyin
        setFormData({
            firstName: '',
            lastName: '',
            phone: '',
            email: ''
        });
        setShowForm(false);
    };

    const navitems = [
        { link: "Solutions", path: "#Solutions" },
        { link: "Products", path: "#Products" },
        { link: "Industries", path: "#Industries" },
        { link: "About", path: "#About" },
        { link: "Policy", path: "#Policy" }
    ];

    return (
        <>
            <nav className='bg-white md:px-14 py-2 max-w-screen-2xl mx-auto border-b text-purple-600 fixed top-0 left-0 right-0 z-50'>
                <div className=' container mx-auto flex justify-between items-center font-medium'>
                    <div className='flex space-x-14 items-center'>
                        <a href="/" className='text-2xl flex item-center space-x-3 ' ><img className='w-5' src={atom} alt="Atom Logo" /></a>
                        <ul className='md:flex space-x-12 hidden'>
                            {navitems.map(({ link, path }) => (
                                <a key={link} href={path} className=' block hover:text-gray-300'>{link}</a>
                            ))}
                        </ul>
                    </div>
                    <div className='space-x-12 hidden md:flex items-center '>
                        <a href="/" className='hidden lg:flex items-center bg-purple-100 hover:bg-purple-700 transition-all duration-300 rounded py-2 px-4 text-purple-800 hover:text-white  ml-52'><span>Log in</span></a>
                        <button className='hidden lg:flex items-center bg-purple-100 hover:bg-purple-700 transition-all duration-300 rounded py-2 px-4 text-purple-800 hover:text-white  ml-52' onClick={toggleForm}><span>Book Demo</span></button>
                    </div>
                    <div className='md:hidden'>
                        <button onClick={toggleMenu} className='text-white focus:outline-none focus:text-gray-300'>
                            {ismenuopen ? (<FaXmark className='w-6 h-6 text-black'/>) : (<FaBars className='w-6 h-6 text-purple-500'/>)}
                        </button>
                    </div>
                </div>
            </nav>
            <div className={`space-y-4 px-4 pb-5 pt-24 bg-purple-600 ${ismenuopen ? 'block fixed top-0 right-0 left-0 ':'hidden'}`}>
                {navitems.map(({ link, path }) => (
                    <a key={link} href={path} className=' block hover:text-gray-300'>{link}</a>
                ))}
            </div>
            {showForm && (
                <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-900 bg-opacity-75 z-50">
                    <div className="bg-white p-8 rounded shadow-lg max-w-md"> {/* Maksimum genişlik burada ayarlandı */}
                        <h2 className="text-2xl font-bold mb-4">Book a Demo</h2>
                        <form onSubmit={handleSubmit} className="flex flex-col items-center">
                            <div className="mb-4 w-full">
                                <label htmlFor="firstName" className="block text-gray-800 font-semibold mb-1">First Name</label>
                                <input type="text" id="firstName" name="firstName" value={formData.firstName} onChange={handleChange} className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <div className="mb-4 w-full">
                                <label htmlFor="lastName" className="block text-gray-800 font-semibold mb-1">Last Name</label>
                                <input type="text" id="lastName" name="lastName" value={formData.lastName} onChange={handleChange} className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <div className="mb-4 w-full">
                                <label htmlFor="phone" className="block text-gray-800 font-semibold mb-1">Phone</label>
                                <input type="tel" id="phone" name="phone" value={formData.phone} onChange={handleChange} className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <div className="mb-4 w-full">
                                <label htmlFor="email" className="block text-gray-800 font-semibold mb-1">Email</label>
                                <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <button type="submit" className="bg-purple-500 text-white py-2 px-4 rounded hover:bg-purple-600">Submit</button>
                        </form>
                    </div>
                </div>
            )}
            {showLogin && (
                <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-900 bg-opacity-75 z-50">
                    <div className="bg-white p-8 rounded shadow-lg max-w-md">
                        <h2 className="text-2xl font-bold mb-4">Login</h2>
                        <form className="flex flex-col items-center">
                            <div className="mb-4 w-full">
                                <label htmlFor="username" className="block text-gray-800 font-semibold mb-1">Username</label>
                                <input type="text" id="username" name="username" className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <div className="mb-4 w-full">
                                <label htmlFor="password" className="block text-gray-800 font-semibold mb-1">Password</label>
                                <input type="password" id="password" name="password" className="block w-full border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500 p-2" />
                            </div>
                            <button type="submit" className="bg-purple-500 text-white py-2 px-4 rounded hover:bg-purple-600">Login</button>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
}

export default Navbar;
